private void parseAndExecute(String command){
        String[]params=command.split("!");
        if(params.length!=2){
        cliButton.setForeground(alertColor);
        cli.setText("port!message(param1, param2, param3)");
        return;
        }

        if(params[0].equals("give_values")) {
if (params[1].startsWith("temperature")) {
params[1] = params[1].substring("temperature".length(), params[1].length());
if (!(params[1].startsWith("(") && params[1].endsWith(")"))) {
cliButton.setForeground(alertColor);
cli.setText("port!message(param1, param2, param3)");
return;
}
params = params[1].substring(1,params[1].length()-1).split(",");
if (!(params.length == 3)) {
cliButton.setForeground(alertColor);
cli.setText("port!message(param1, param2, param3)");
return;
}
try {
Map<String, Object> param = new HashMap<String, Object>();
param.put("id", StringHelper.toObject (short.class, params[0].trim()));
param.put("txt", StringHelper.toObject (String.class, params[1].trim()));
param.put("t", StringHelper.toObject (float.class, params[2].trim()));
Command c = new Command(port_TempSim_give_values, temperatureType, param);c.execute();
((DefaultListModel)commands.getModel()).addElement(c);
cliButton.setForeground(Color.BLACK);
} catch(IllegalArgumentException iae) {
System.err.println("Cannot parse arguments for message temperature on port give_values. Please try again with proper parameters");
cliButton.setForeground(alertColor);
}
}
}


        else{
        cliButton.setForeground(alertColor);
        cli.setText("port!message(param1, param2, param3)");
        }
        }

